import React, { useState } from "react"
import { Link, useHistory } from "react-router-dom"
import { createDeck } from "./utils/api/index"

function CreateDeck () {
    const history = useHistory()
    const initialState = {
        name: "",
        description: "",
    }
    const [newDeck, setNewDeck] = useState(initialState)

    function handleChange({target}) {
        setNewDeck({
            ...newDeck,
            [target.name]: target.value
        })
    }

    async function handleSubmit(event) {
        event.preventDefault()
        const abortController = new AbortController()
        const response = await createDeck({...newDeck}, abortController.signal)
        history.push("/")
        return response
    }

    async function handleCancel() {
        history.push('/')
    }


    return (
        <div>
            <ol className="breadcrumb">
                <li className="breadcrumb-item"><Link to="/">Home</Link></li>
                <li className="breadcrumb-item active">Create Deck</li>
            </ol>
            <form onSubmit={(event) => handleSubmit(event)}>
                <h1>Create Deck</h1>
                <div className="form-group">
                    <label>
                        Name
                    </label>
                    <input 
                        id="name"
                        name="name"
                        onChange={handleChange} 
                        type="text"
                        value={newDeck.name}
                    />
                </div>
                <div className="form-group">
                    <label>
                        Description
                    </label>
                    <textarea 
                        id="description"
                        name="description"
                        onChange={handleChange} 
                        type="text"
                        value={newDeck.description}
                    />
                </div>
                <a className="btn btn-secondary" onClick={() => handleCancel()}>Cancel</a>
                <a className="btn btn-primary" type="submit">Submit</a>
            </form>
        </div>
    )
}

export default CreateDeck
