import React, { useState, useEffect } from "react"
import { listDecks, listCards } from "./utils/api/index"
import { Link } from "react-router-dom"
import Delete from "./Delete"

function Home () {
    const [decks, setDecks] = useState([])
    useEffect (() => {
        async function fetchData() {
            const abortController = new AbortController()
            const response = await listDecks(abortController.signal)
            setDecks(response)
            return() => {
                abortController.abort()
            }
        }
        fetchData()
    }, [])

    const [cards, setCards] = useState([])
    useEffect (() => {
        async function fetchData() {
            const abortController = new AbortController()
            const response = await listCards(deckId, abortController.signal)
            setDecks(response)
            return() => {
                abortController.abort()
            }
        }
        fetchData()
    }, [deckId])

    return (
        <div className="container">
            <Link className="btn btn-secondary" to="/decks/new">
                Create Deck
            </Link>
            <div className="card-deck">
                {decks.map((deck) => {
                    return (
                        <div className="card" style={{ width: '30rem'}} key={`${deck.id}`}>
                            <div className="card-body">
                                <div className="card-title">
                                    {`${deck.name}`}
                                </div>
                                <div className="card-subtitle mb-2 text-muted">
                                    {`${cards.length} cards`}
                                </div>
                                <div className="card-text">
                                    {`${deck.description}`}
                                </div>
                                <Link className="btn btn-secondary" to={`/decks/${deck.id}`}>
                                    View
                                </Link>
                                <Link className="btn btn-primary" to={`/decks/${deck.id}/study`}>
                                    Study
                                </Link>
                                <Delete array={decks} deckId={deck.id} setDecks={setDecks} />
                            </div>
                        </div>
                    )
                })}
            </div>
        </div>
    )

}

export default Home
