import React, {useState, useEffect} from "react"
import {listCards, readCard} from "./utils/api/index.js"

function Card({deck}) {
    const [cards, setCards] = useState([])
    const [cardId, setCardId] = useState(1)
    const [cardSide, setCardSide] = useState("front")

    useEffect (() => {
        async function fetchData() {
            const abortController = new AbortController()
            const response = await listCards(deck.id, abortController.signal)
            setCards(response)
            return() => {
                abortController.abort()
            }
        }
        fetchData()
    }, [])

    const flip = () => {
        if (cardSide === "front") {
            setCardSide("back")
        } else if (cardSide === "back") {
            setCardSide("front")
        }
    }

    return (
        cards.filter((card) => {
            if (card.id === cardId) {
                console.log(card[cardSide])
                return(
                    <div className="card">
                        <div className="card-body">
                            <div className="card-title">
                                Card {card.id} of {deck.length}
                            </div>
                            <div className="card-text">
                                {card[cardSide]}
                            </div>
                            <div className="btn btn-secondary">Flip</div>
                        </div>
                    </div>
                )      
            }          
        })
    )
}

export default Card
