import React, {useState, useEffect} from "react"
import {listCards, readCard} from "./utils/api/index.js"

function Card({deck}) {
    const [cards, setCards] = useState([])

    useEffect (() => {
        async function fetchData() {
            const abortController = new AbortController()
            const response = await listCards(deck.id, abortController.signal)
            setCards(response)
            return() => {
                abortController.abort()
            }
        }
        fetchData()
    }, [])

    return (
        cards.map((card) => {
            return(
                <div className="card">
                    <div className="card-body">
                        <div className="card-title">
                            Card {card.id} of {deck.length}
                        </div>
                        <div className="card-text">
                            {card.front}
                        </div>
                        <div className="btn btn-secondary">Flip</div>
                    </div>
                </div>
            )                
        })
    )
}

export default Card
