import React, { useState, useEffect } from "react"
import { listDecks, listCards, deleteDeck } from "./utils/api/index"
import { Link, useHistory } from "react-router-dom"

function Home () {
    const history = useHistory()
    const [decks, setDecks] = useState([])

    useEffect (() => {
        async function fetchData() {
            const abortController = new AbortController()
            try {
                const deckResponse = await listDecks(abortController.signal)
                setDecks(deckResponse)
                decks.map((deck) => {
                    const cards = getCards(deck)
                        .then((result) => {
                            console.log(result)
                        })
                        .catch((error) => {
                            console.error('Something went wrong', error)
                        })
                })
            } catch (error) {
                console.error('Something went wrong', error)
            }
            return() => {
                abortController.abort()
            }
        }
        fetchData()
    }, [])

    async function getCards(deck) {
        return await listCards(deck.id)
    }

    async function handleDelete(deck) {
        if (window.confirm(`Delete this deck? You will not be able to recover it`)) {
            history.go(0)
            return await deleteDeck(deck.id)
        }
    }

    return (
        <div className="container">
            <Link className="btn btn-secondary" to="/decks/new">
                Create Deck
            </Link>
            <div className="card-deck">
                {decks.map((deck) => {
                    return (
                        <div className="card" style={{ width: '30rem'}} key={`${deck.id}`}>
                            <div className="card-body">
                                <div className="card-title">
                                    {`${deck.name}`}
                                </div>
                                <div className="card-subtitle mb-2 text-muted">
                                    {`${deck.count} cards`}
                                </div>
                                <div className="card-text">
                                    {`${deck.description}`}
                                </div>
                                <Link className="btn btn-secondary" to={`/decks/${deck.id}`}>
                                    View
                                </Link>
                                <Link className="btn btn-primary" to={`/decks/${deck.id}/study`}>
                                    Study
                                </Link>
                                <button type="button" className="btn btn-danger" onClick={() => handleDelete(deck)}>Delete</button>
                             </div>
                        </div>
                    )
                })}
            </div>
        </div>
    )

}

export default Home
