import React, { useEffect, useState } from "react"
import { Link, useParams, useHistory } from "react-router-dom"
import { listCards, readDeck, readCard } from "./utils/api/index"


function Study () {
    const {deckId} = useParams()
    const [deck, setDeck] = useState({})
    const [cards, setCards] = useState([])
    const [cardNumber, setCardNumber] = useState(1)
    const [front, isFront] = useState(true)
    const history = useHistory()

    useEffect (() => {
        async function fetchData() {
            const abortController = new AbortController()
            const response = await readDeck(deckId, abortController.signal)
            setDeck(response)
            return() => {
                abortController.abort()
            }
        }
        fetchData()
    }, [])

    useEffect (() => {
        async function fetchData() {
            const abortController = new AbortController()
            const response = await listCards(deckId, abortController.signal)
            setCards(response)
            return() => {
                abortController.abort()
            }
        }
        fetchData()
    }, [deckId])

    function nextCard(index, total) {
        console.log(index)
        if (index < total) {
            setCardNumber(cardNumber + 1)
        } else {
            if (window.confirm(`Restart cards? Click 'cancel' to return to the home page`)) {
                setCardNumber(1)
            } else {
                history.push("/")
            }
        }
    }

    function flipCard() {
        if (front) {
            isFront(false)
        } else {
            isFront(true)
        }
    }

    return (
        <div>
            <ol className="breadcrumb">
                <li className="breadcrumb-item"><Link to="/">Home</Link></li>
                <li className="breadcrumb-item"><Link to={`/decks/${deckId}`}>{deck.name}</Link></li>
                <li className="breadcrumb-item active">Study</li>
            </ol>
            <div>
                <h2>{`Study: ${deck.name}`}</h2>
                    <div className="card">
                        {cards.map((card, index) => {
                            if (index === cardNumber - 1) {
                                return (
                                    <div className="card-body">
                                        <div className="card-title">
                                            {`Card ${index + 1} of ${cards.length}`}
                                        </div>
                                        {front ? card.front : card.back}
                                        <button onClick={flipCard} className="btn btn-secondary">
                                            Flip
                                        </button>
                                        <button onClick={() => nextCard(index + 1, cards.length)} className="btn btn-primary">
                                            Next
                                        </button>
                                    </div>
                                )
                            }
                        })}

                    </div>
            </div>
        </div>
    )
}

export default Study
