import React, { useState, useEffect } from "react"
import { listDecks } from "./utils/api/index"
import { Switch, Route, Link } from "react-router-dom"
import Study from "./Study"
import Deck from "./Deck"
import CreateDeck from "./CreateDeck"

function Home () {
    const [decks, setDecks] = useState([])

    useEffect (() => {
        async function fetchData() {
            const abortController = new AbortController()
            const response = await listDecks(abortController.signal)
            setDecks(response)
            return() => {
                abortController.abort()
            }
        }
        fetchData()
    }, [])

    async function deleteDeck(deckId) {
        if (window.confirm(`Delete this deck?

You will not be able to recover it.`)) {        
            const filteredDecks = decks.filter((deck) => deck.id !== deckId)
            setDecks(filteredDecks)
        }
    }

    function allDecks() {
        return (
            <div className="card-deck">
                {decks.map((deck) => {
                    return (
                        <div className="card" style={{ width: '30rem'}} key={`${deck.id}`}>
                            <div className="card-body">
                                <div className="card-title">
                                    {`${deck.name}`}
                                </div>
                                <div className="card-subtitle mb-2 text-muted">
                                    {`${'?'} cards`}
                                </div>
                                <div className="card-text">
                                    {`${deck.description}`}
                                </div>
                                <Link to={`/decks/${deck.id}`}>
                                    <button type="button" className="btn btn-secondary">View</button>
                                </Link>
                                <Link to={`/decks/${deck.id}/study`}>
                                    <button type="button" className="btn btn-primary">Study</button>
                                </Link>
                                <Link to={`#`}>
                                    <button type="button" className="btn btn-danger" onClick={deleteDeck(deck.id)}>Delete</button>
                                </Link>
                            </div>
                        </div>
                    )
                })}
            </div>
        )
    }

    return (
        <div className="container">
            <Switch>
                <Route path="/">
                    <Link to="/decks/new">
                        <button type="button" className="btn btn-secondary">Create Deck</button>
                    </Link>
                    {allDecks()}
                </Route>
                <Route path="/decks/new">
                    <CreateDeck />
                </Route>
                <Route path="/decks/:deckId">
                    <Deck />
                </Route>
                <Route path="/decks/:deckId/study">
                    <Study />
                </Route>
            </Switch>
        </div>
    )

}

export default Home
