import React, { useState, useEffect } from "react"
import { listDecks, deleteDeck } from "./utils/api/index"
import { Link } from "react-router-dom"
import Deck from "./Deck"

function Home () {
    const [decks, setDecks] = useState([])
    useEffect (() => {
        async function fetchData() {
            const abortController = new AbortController()
            const response = await listDecks(abortController.signal)
            setDecks(response)
            return() => {
                abortController.abort()
            }
        }
        fetchData()
    }, [])

    const handleDelete = async(deck) => {
        if (window.confirm(`Delete this deck? You will not be able to recover it`)) {
        const abortController = new AbortController()
        return await deleteDeck(deck.id, abortController.signal)
        }
    }

    return (
        <div className="container">
            <Link className="btn btn-secondary" to="/decks/new">
                Create Deck
            </Link>
            <div className="card-deck">
                {decks.map((deck) => {
                    return (
                        <div className="card" style={{ width: '30rem'}} key={`${deck.id}`}>
                            <div className="card-body">
                                <div className="card-title">
                                    {`${deck.name}`}
                                </div>
                                <div className="card-subtitle mb-2 text-muted">
                                    {`${0} cards`}
                                </div>
                                <div className="card-text">
                                    {`${deck.description}`}
                                </div>
                                <Link className="btn btn-secondary" to={`/decks/${deck.id}`}>
                                    View
                                </Link>
                                <Link className="btn btn-primary" to={`/decks/${deck.id}/study`}>
                                    Study
                                </Link>
                                <button type="button" className="btn btn-danger" onClick={handleDelete({deck})}>Delete</button>
            </div>
        </div>
                    )
                })}
            </div>
        </div>
    )

}

export default Home
