import React, { useState, useEffect } from "react"
import { listDecks } from "./utils/api/index"
import { Switch, Route, Link } from "react-router-dom"
import Study from "./Study"
import Deck from "./Deck"
import CreateDeck from "./CreateDeck"
import Delete from "./Delete"

function Home () {
    const [decks, setDecks] = useState([])

    useEffect (() => {
        async function fetchData() {
            const abortController = new AbortController()
            const response = await listDecks(abortController.signal)
            setDecks(response)
            return() => {
                abortController.abort()
            }
        }
        fetchData()
    }, [])

    function allDecks() {
        return (
            <div className="card-deck">
                {decks.map((deck) => {
                    return (
                        <div className="card" style={{ width: '30rem'}} key={`${deck.id}`}>
                            <div className="card-body">
                                <div className="card-title">
                                    {`${deck.name}`}
                                </div>
                                <div className="card-subtitle mb-2 text-muted">
                                    {`${'?'} cards`}
                                </div>
                                <div className="card-text">
                                    {`${deck.description}`}
                                </div>
                                <Link className="btn btn-secondary" to={`/decks/${deck.id}`}>
                                    View
                                </Link>
                                <Link className="btn btn-primary" to={{ pathname: `/decks/${deck.id}/study`, state: { passedDeck: deck }}}>
                                    Study
                                </Link>
                                <Delete array={decks} deckId={deck.id} setDecks={setDecks} />
                            </div>
                        </div>
                    )
                })}
            </div>
        )
    }

    return (
        <div className="container">
            <Switch>
                <Route exact path="/">
                    <Link className="btn btn-secondary" to="/decks/new">
                        Create Deck
                    </Link>
                    {allDecks()}
                </Route>
                <Route path="/decks/new">
                    <CreateDeck />
                </Route>
                <Route path="/decks/:deckId">
                    <Deck />
                </Route>
                <Route path="/decks/:deckId/study">
                    <Study />
                </Route>
            </Switch>
        </div>
    )

}

export default Home
