import React, {useState, useEffect} from "react"
import {listCards, readCard} from "./utils/api/index.js"

function Card({deck}) {
    const [cards, setCards] = useState([])
    const [currentIndex, setCurrentIndex] = useState(1)
    const [deckLength, setdeckLength] = useState(0)
    const [currentFace, setCurrentFace] = useState("front")

    useEffect (() => {
        async function fetchData() {
            const abortController = new AbortController()
            const response = await listCards(deck.id, abortController.signal)
            setCards(response)
            setdeckLength(cards.length)
            return() => {
                abortController.abort()
            }
        }
        fetchData()
    }, [])

    async function flipCard() {
        if(currentIndex === cards.length) {
            if(window.confirm(
                `Restart cards?

                Click 'cancel' to return to the home page`
            )) {

            }
        }
        if(currentFace === "front") {
            setCurrentFace("back")
        } else if (currentFace ==="back") {
            setCurrentFace("front")
        }
    }

    async function nextCard() {
        if(currentIndex < cards.length) {
            setCurrentIndex(currentIndex+1)
        }
    }

    function showFace() {
        if(currentFace === "front") {
            return (
                <div className="card-text">
                    {cards[currentIndex].front}
                </div>
            )
        } else if(currentFace === "back") {
            return (
                <div className="card-text">

                </div>
            )
        }
    }

    return(
        <div className="card">
            <div className="card-body">
                <div className="card-title">
                    {`Card ${currentIndex} of ${deckLength}`}
                </div>
                    {showFace}
                <button onClick={flipCard} className="btn btn-secondary">
                    Flip
                </button>
                <button onClick={nextCard} className="btn btn-primary">
                    Next
                </button>
            </div>
        </div>
    )
}

export default Card
