import React, {useState, useEffect} from "react"
import {listCards, readCard} from "./utils/api/index.js"

function Card({deck}) {
    const [cards, setCards] = useState([])
    const [card, setCard] = useState({})

    useEffect (() => {
        async function fetchData() {
            const abortController = new AbortController()
            const response = await listCards(deck.id, abortController.signal)
            setCard(response)
            return() => {
                abortController.abort()
            }
        }
        fetchData()
    }, [])

    useEffect (() => {
        async function fetchData() {
            const abortController = new AbortController()
            const response = await readCard(card, abortController.signal)
            setCard(response)
            return() => {
                abortController.abort()
            }
        }
        fetchData()
    }, [])

    return (
        <div className="card">
            <div className="card-body">
                <div className="card-title">
                    Card {card.id} of {deck.length}
                </div>
                <div className="card-text">
                    {card.front}
                </div>
            </div>
        </div>
    )
}

export default Card
